import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface Cobranca {
  id: number;
  nome: string;
  paginas: number;
  alertas: number;
  atrasos: number;
}

@Component({
  selector: 'app-cobrancas',
  templateUrl: './cobrancas-table.component.html',
  styleUrls: ['./cobrancas-table.component.css']
})
export class CobrancasTableComponent implements OnInit {
  cobrancas: Cobranca[] = [];
  newCobranca: Cobranca = { id: 0, nome: '', paginas: 0, alertas: 0, atrasos: 0 };

  private apiUrl = 'https://api.example.com/cobrancas';

  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    this.loadCobrancas();
  }

  loadCobrancas(): void {
    this.http.get<Cobranca[]>(this.apiUrl).subscribe(data => {
      this.cobrancas = data;
    });
  }

  addCobranca(): void {
    this.http.post<Cobranca>(this.apiUrl, this.newCobranca).subscribe(cobranca => {
      this.cobrancas.push(cobranca);
      this.newCobranca = { id: 0, nome: '', paginas: 0, alertas: 0, atrasos: 0 };
    });
  }

  updateCobranca(cobranca: Cobranca): void {
    this.http.put<Cobranca>(`${this.apiUrl}/${cobranca.id}`, cobranca).subscribe();
  }

  deleteCobranca(cobrancaId: number): void {
    this.http.delete(`${this.apiUrl}/${cobrancaId}`).subscribe(() => {
      this.cobrancas = this.cobrancas.filter(c => c.id !== cobrancaId);
    });
  }
}
